def getRandomCode():
    cmd = ["docker", "run","holiman/testeth","--randomcode","100"]
    import subprocess
    #print(" ".join(cmd))
    return subprocess.check_output(cmd).strip().decode("utf-8")[2:]

def testCode(code, gas=0xffffffff):
    if len(code) == 0:
        print("Err, no code!")
        return False
    (difference, trace, g_cmd, p_cmd) = execute(code, gas )

    if not difference : 
        print("Ok")
        return False


    print("Diff found")
    print("\n".join(trace))
    print("")
    print(g_cmd)
    print(p_cmd)
    return True

def fuzz(gas):

    diff_found = False
    while not diff_found:
        print("----Fuzzing--- ")
        diff_found = testCode(getRandomCode(), gas)
        print("-------- ")



